INSERT INTO k8s_posts VALUES ('654', '1', '2019-01-14 12:28:02', '2019-01-14 04:28:02', '<img class=\"size-full wp-image-653 aligncenter\" src=\"https://www.yunfuzg.com/wp-content/uploads/2019/01/k8s1.jpg\" alt=\"\" width=\"360\" height=\"202\" />\r\n<ul>\r\n 	<li>\r\n<h3>Pod exec探针</h3>\r\n</li>\r\n</ul>\r\n<pre>[cce]\r\napiVersion: v1\r\nkind: Pod\r\nmetadata:\r\n  name: liveness-exec\r\n  namespace: default\r\nspec:\r\n  containers:\r\n  - name: liveness-exec\r\n    image: busybox:latest\r\n    imagePullPolicy: IfNotPresent\r\n    command: [\"/bin/sh\",\"-c\",\"touch /tmp/index; sleep 30; rm -rf /tmp/index; sleep 3600\"]\r\n    livenessProbe:\r\n      exec:\r\n        command: [\"test\",\"-e\",\"/tmp/index\"]\r\n      initialDelaySeconds: 1\r\n      periodSeconds: 3\r\n[/cce]</pre>\r\n<ul>\r\n 	<li>\r\n<h3>Pod httpget探针</h3>\r\n</li>\r\n</ul>\r\n<pre>[cce]\r\napiVersion: v1\r\nkind: Pod\r\nmetadata:\r\n  name: liveness-httpget\r\n  namespace: default\r\nspec:\r\n  containers:\r\n  - name: liveness-httpget\r\n    image: ikubernetes/myapp:v1\r\n     imagePullPolicy: IfNotPresent\r\n    ports:\r\n    - name: http\r\n      containerPort: 80\r\n    imagePullPolicy: IfNotPresent\r\n    livenessProbe:\r\n      httpGet:\r\n        port: http\r\n        path: /index.html\r\n      initialDelaySeconds: 1\r\n      periodSeconds: 3\r\n[/cce]</pre>\r\n<ul>\r\n 	<li>\r\n<h3>Pod 就绪状态httpgat探针</h3>\r\n</li>\r\n</ul>\r\n<pre>[cce]\r\napiVersion: v1\r\nkind: Pod\r\nmetadata:\r\n  labels:\r\n    test: redineness\r\n  name: redineness\r\nspec:\r\n  restartPolicy: OnFailure\r\n  containers:\r\n  - name: redineness\r\n    image: busybox\r\n     imagePullPolicy: IfNotPresent\r\n    ports:\r\n    - name: http\r\n      containerPort: 80\r\n    readinessProbe:\r\n      httpGet:\r\n        port: http\r\n        path: /index.html\r\n      initialDelaySeconds: 10\r\n      periodSeconds: 5\r\n[/cce]</pre>\r\n<ul>\r\n 	<li>\r\n<h3>Pod启动前</h3>\r\n</li>\r\n</ul>\r\n<pre>[cce]\r\napiVersion: v1\r\nkind: Pod\r\nmetadata:\r\n  name: poststart-pod\r\n  namespace: default\r\nspec:\r\n  containers:\r\n  - name: busybox-http\r\n    image: busybox\r\n    imagePullPolicy: IfNotPresent\r\n    lifecycle:\r\n      postStart:\r\n        exec:\r\n          command: [\"\"]\r\n#    command: [\"/bin/httpd\"]\r\n#    args: [\"-f\",\"-h /data/web/html\" ]\r\n[/cce]</pre>', 'Kubernetes Pod exec httpget探针', '', 'inherit', 'closed', 'closed', '', '647-revision-v1', '', '', '2019-01-14 12:28:02', '2019-01-14 04:28:02', '', '647', 'https://www.yunfuzg.com/647-revision-v1.html', '0', 'revision', '', '0');
